def solution(n):
    i = int(n)
    j = 0

    while i > 1:
        if i & 1 == 1:
            if i % 4 == 1 or i == 3:
                i -= 1
            else:
                i += 1
        else:
            i = i >> 1
        j += 1
    return j

print(solution("4"))
print(solution("15"))
print(solution("16"))
print(solution("36"))
print(solution("56"))
print(solution("57"))
print(solution(""))